import classes from "./Cart.module.css";
import Modal from '../UI/Modal'
import { useContext, useState } from "react";
import CartItem from './CartItem'
import CartContext from "../../store/cart-context";

const Cart = (props) => {
 const cartCtx = useContext(CartContext)

 const[amount, setAmount]=useState(cartCtx.totalAmount)
 const hasItems = cartCtx.items.length>0

 const onOrderHandler=()=>{
   setAmount(0)
 }
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
       <CartItem key={item.id} amount={item.amount} name={item.name} price={item.price}></CartItem>
      ))}
    </ul>
    );

  return (
    <Modal onClose={props.onClose}>
      {cartItems}
      <div className={classes.total}>
        <span>Total amount</span>
        <span>{amount.toFixed(2)}</span>
      </div>
      <div className={classes.actions}>
          <button onClick={props.onClose} className={classes['button--alt']}>Close</button>
          {hasItems && <button className={classes.button} onClick={onOrderHandler}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
