import classes from "./Checkout.module.css";
import { useState, useRef } from "react";

const isEmpty = (value) => value.trim().length === "";

const isFiveChars = (value) => value.trim().length <= 5;

const Checkout = (props) => {
  const [formValidity, setFormValidity] = useState({
    name: true,
    street: true,
    postal: true,
    city: true,
  });

  const nameInputRef = useRef();
  const streetInputRef = useRef();
  const postalInputRef = useRef();
  const cityInputRef = useRef();

  const nameIsValid = !isEmpty(nameInputRef);
  const streetIsValid = !isEmpty(streetInputRef);
  const postalIsValid = isFiveChars(postalInputRef);
  const cityIsValid = !isEmpty(cityInputRef);

  setFormValidity({
    name: nameIsValid,
    street: streetIsValid,
    postal: postalIsValid,
    city: cityIsValid,
  });

  const submitHandler = (event) => {
    event.preventDefault();
    const formIsValid =
      nameIsValid && streetIsValid && postalIsValid && cityIsValid;

    if (!formIsValid) {
      alert("Form is not valid");
      return;
    }
    alert("Sucessful order");
  };

  return (
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Input your name</label>
        <input type="text" id="name" ref={nameInputRef}></input>
        {!formValidity.name && <p>The name field is empty</p>}
      </div>

      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input type="text" id="street" ref={streetInputRef}></input>
        {!formValidity.street && <p>The street field is empty</p>}
      </div>

      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input type="text" id="postal" ref={postalInputRef}></input>
        {!formValidity.postal && <p>The postal code field is empty</p>}
      </div>

      <div className={classes.control}>
        <label htmlFor="City">City</label>
        <input type="text" id="City" ref={cityInputRef}></input>
        {!formValidity.city && <p>The city field is empty</p>}
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
