import classes from "./Checkout.module.css";
import { useState } from "react";

const isEmpty = (value) => {
  return value.trim().length === "";
};

const isFiveChars = (value) => {
  return value.trim().length <= 5;
};

const Checkout = (props) => {
  const[formValidity, setFormValidity]=useState({
    name: true,
    street: true,
    postal: true,
    city: true
  })
  const [name, setName] = useState("");
  const [street, setStreet] = useState("");
  const [postal, setPostal] = useState("");
  const [city, setCity] = useState("");
  const nameInputHandler = (event) => {
    setName(event.target.value);
  };
  const streetInputHandler = (event) => {
    setStreet(event.target.value);
  };
  const postalInputHandler = (event) => {
    setPostal(event.target.value);
  };
  const cityInputHandler = (event) => {
    setCity(event.target.value);
  };

  const nameIsValid = !isEmpty(name);
  const streetIsValid = !isEmpty(street);
  const postalIsValid = isFiveChars(postal);
  const cityIsValid = !isEmpty(city);

  setFormValidity({
    name: nameIsValid,
    street: streetIsValid,
    postal: postalIsValid,
    city: cityIsValid
  })

  const submitHandler = (event) => {
    event.preventDefault();
    const formIsValid= nameIsValid && streetIsValid && postalIsValid && cityIsValid

    if(!formIsValid){
      alert('Form is not valid')
      return
    }
    alert('Sucessful order')

    setName('')
    setStreet('')
    setPostal('')
    setCity('')

  };

  return (
    <form onSubmit={submitHandler}>
      <div className={classes.control}>
        <label htmlFor="name">Input your name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputHandler}
          value={name}
        ></input>
        {!formValidity.name&& <p>The name field is empty</p>}
      </div>

      <div className={classes.control}>
        <label htmlFor="street">Street</label>
        <input
          type="text"
          id="street"
          onChange={streetInputHandler}
          value={street}
        ></input>
        {!formValidity.street&& <p>The street field is empty</p>}
      </div>

      <div className={classes.control}>
        <label htmlFor="postal">Postal Code</label>
        <input
          type="text"
          id="postal"
          onChange={postalInputHandler}
          value={postal}
        ></input>
         {!formValidity.postal&& <p>The postal code field is empty</p>}
      </div>

      <div className={classes.control}>
        <label htmlFor="City">City</label>
        <input
          type="text"
          id="City"
          onChange={cityInputHandler}
          value={city}
        ></input>
         {!formValidity.city&& <p>The city field is empty</p>}
      </div>
      <div className={classes.actions}>
        <button type="button" onClick={props.onCancel}>
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
